import { Flex, Box, Heading, FormControl, FormLabel, Button, Input, Alert} from "@chakra-ui/react"
import {useFormik} from 'formik';
import validationSchema from "./validations";
import {fetchRegister} from '../../../api';
import {useAuth} from '../../../contexts/AuthContext'


function Signup() {

    const {login} = useAuth();

    const formik = useFormik({
        initialValues: {
            email: "",
            password: "",
            passwordConfirm: "",
        },
        validationSchema,
        onSubmit: async (values, bag) => {
            try{
                const registerResponse = await fetchRegister({
                    email: values.email,
                    password: values.password,
                });
              console.log(registerResponse);
              login(registerResponse);
            } catch (e) {
                bag.setErrors({general: e.response.data.message})
            }
        },
    });

    return (
        <div>
            <Flex align="center" width="full" justifyContent="center">
                <Box pt={10}>
                    <Box textAlign="center">
                        <Heading>Sign Up</Heading>
                    </Box>
                    <Box my={5}>
                        {formik.errors.general && (<Alert status="error">
                            {formik.errors.general}
                        </Alert>)}
                    </Box>
                    <Box my={5} textAlign="left">
                        <form onSubmit={formik.handleSubmit}>
                            <FormControl>
                                <FormLabel>Email</FormLabel>
                                <Input name="email" 
                                onChange={formik.handleChange} 
                                onBlur={formik.handleBlur}
                                value={formik.values.email}
                                isInvalid={formik.touched.email && formik.errors.email}></Input>
                            </FormControl>
                            <FormControl mt={4}>
                                <FormLabel>Password</FormLabel>
                                <Input name="password" type="password"
                                onChange={formik.handleChange} 
                                onBlur={formik.handleBlur}
                                value={formik.values.password}
                                    isInvalid={formik.touched.password && formik.errors.password}></Input>
                            </FormControl>
                            <FormControl mt={4}>
                                <FormLabel>Password Confirmation</FormLabel>
                                <Input name="passwordConfirm" type="password"
                                onChange={formik.handleChange} 
                                onBlur={formik.handleBlur}
                                value={formik.values.passwordConfirm}
                                isInvalid={formik.touched.passwordConfirm && formik.errors.passwordConfirm}></Input>
                            </FormControl>
                            <Button mt={4} width="full" type="submit">
                                Sign Up
                            </Button>
                        </form>
                    </Box>
                </Box>    
            </Flex>
        </div>
    )
}

export default Signup;
